1. Use a set of pairs to store island coordinates (A, B)
2. Use [flood fill](https://en.wikipedia.org/wiki/Flood_fill) to find islands
3. Simply calculate distance between all points in the islands and return the minimum

Check out my YouTube channel for more solutions: [https://youtube.com/c/codereport](https://youtube.com/c/codereport)

```

#define FORI(s,n) for(int i = s; i < n; i++)
#define FORJ(s,n) for(int j = s; j < n; j++)

using vi  = vector<int>;
using vvi = vector<vi>;
using island = set<pair<int, int>>;

class Solution {
public:

   int n;
   vi dx = { 0, 0, 1, -1 };
   vi dy = { 1, -1, 0, 0 };

   bool is_valid(int x, int y) {
      return (x >= 0 && x < n && y >= 0 && y < n);
   }

   void flood_fill(island& A, const vvi& g, int x, int y) {
      A.insert({ x, y });
      FORI(0, 4) {
         int nx = x + dx[i];
         int ny = y + dy[i];
         if (is_valid(nx, ny) && g[nx][ny] && !A.count({ nx, ny }))
            flood_fill(A, g, nx, ny);
      }
   }

   template<class T>
   int dist(T a, T b) {
      return abs(a.first - b.first) + abs(a.second - b.second) - 1;
   }

   int shortestBridge(vvi& g) {
      n = g.size();
      island A, B;

      // discover the islands using flood fill
      FORI(0, n) {
         FORJ(0, n) {
            if (g[i][j] == 0) continue;
            if (A.empty())
               flood_fill(A, g, i, j);
            else if (B.empty() && !A.count({ i, j }))
               flood_fill(B, g, i, j);
         }
      }

      // calculate the distance between points in the islands
      int ans = 2 * n;
      for (auto i : A) {
         for (auto j : B) {
            ans = min(ans, dist(i, j));
         }
      }

      return ans;
   }
};

