We store a set of perimeter nodes of both the islands in two sets. Then return the minimum manhattan distance between the two perimeters.

```
class Solution {
    set<pair<int,int>> perimiter_1;
    set<pair<int,int>> perimiter_2;
    vector<vector<int>> matrix;
    vector<vector<bool>> visited;
    int m,n;
public:
    int shortestBridge(vector<vector<int>>& A) {
        m = A.size();
        n = A[0].size();
        matrix = A;

        visited.resize(m,vector<bool>(n,0));

        int i,j;
        bool first_found = false; // to track if the perimeter is of island 1 or 2.
        for(i = 0; i<m;i++){
            for(j = 0; j<n ; j++){
                if(A[i][j] && !visited[i][j]){
                    find_perimeter(i,j,first_found);
                    first_found = true;
                }
            }
        }

        int result = INT_MAX;

        //compute manhatan distance between all possible perimeter combinations and return minimum distance.
        for(auto& p1 : perimiter_1){
            for(auto& p2 : perimiter_2){
                result = min(result, abs(p1.first - p2.first) + abs(p1.second - p2.second) -1 );
            }
        }
        return result;
    }

    int numofneighbour(int i, int j) 
    { 
        int count = 0; 
        if (i > 0 && matrix[i - 1][j]) 
            count++; 
        if (j > 0 && matrix[i][j - 1]) 
            count++;  
        if (i < m-1 && matrix[i + 1][j]) 
            count++; 
        if (j < n-1 && matrix[i][j + 1]) 
            count++; 

        return count; 
    } 

    void find_perimeter(int i, int j, bool& first_found){
        if(i<0 || j<0 || i>=m||j>=n||matrix[i][j] == 0||visited[i][j])
            return;
        visited[i][j] = 1;

        find_perimeter(i+1,j,first_found);
        find_perimeter(i-1,j,first_found);
        find_perimeter(i,j+1,first_found);
        find_perimeter(i,j-1,first_found);

        // if number of neighbours is less than 4 then it's a perimeter node. Insert it in the appropriate set.
        if(numofneighbour(i,j) < 4){
            if(!first_found)
                perimiter_1.insert(make_pair(i,j));
            else
                perimiter_2.insert(make_pair(i,j));
        }

        return;

    }
};
```

