Hey everyone hope you're having a great day. So about this question , its simply an idea to fill and chage one island to a diffrent type and as there are only 2 of them so we could easily distinguish between the islands.
The idea is we change one island to some dif. colour and then start a bfs from every of its node and see if we could reach the other island and as bfs gives us the shortest path , as soon as we touch the other island we will know that we are on it and could easily return the dist.
```
bool isValid(int i, int j, vector<vector<int>> &grid){   
        if (i < 0 || i == grid.size() || j < 0 || j == grid[0].size() || grid[i][j] == 0 || grid[i][j] == -1)
            return false;
        else
            return true;
    }
    void dfs(int i, int j, vector<vector<int>> &grid , queue<pair<int ,int>>& q){

        if(isValid(i, j, grid) == false)
            return;

        q.push({i,j});

        grid[i][j] = -1;

        dfs(i + 1, j, grid , q);
        dfs(i - 1, j, grid , q);
        dfs(i, j + 1, grid , q);
        dfs(i, j - 1, grid , q);
    }
    int shortestBridge(vector<vector<int>>& grid) {
        int n = grid.size();

        vector<pair<int ,int>> dis = {{1 ,0} , {-1 , 0} , {0 , 1} , {0 , -1}};

        queue<pair<int , int>> q;

        bool brk = false;

        for(int i = 0 ; i < n ; i++){
            for(int j = 0 ; j < n ; j++){
                if(grid[i][j] == 1){
                    dfs(i , j , grid , q);
                    brk = true;
                    break;
                }
            }
            if(brk){
                break;
            }
        }
        int dist = 0;
        while(!q.empty()){
            int size = q.size();
            while(size--){
                auto front = q.front();
                q.pop();

                int x = front.first;
                int y = front.second;

                for(int i = 0 ; i < 4 ; i++){
                    int adj_x = x + dis[i].first;
                    int adj_y = y + dis[i].second;

                    if(adj_x < 0 || adj_y < 0 || adj_x >= n || adj_y >= n || grid[adj_x][adj_y] == -1){
                        continue;
                    }
                    if(grid[adj_x][adj_y] == 1){
                        return dist;
                    }
                    grid[adj_x][adj_y] = -1;
                    q.push({adj_x , adj_y});
                }   
            }
        dist++;
        }
        return -1;
    }
