class Solution {
    vector<vector<int>>vis; //vis vector to keep track of visited cells
    queue<pair<int,int>>q;  //queue to perform bfs from one connected group of cells
    vector<vector<int>> dir = {{0,1},{0,-1},{1,0},{-1,0}};  //dirction vector to move to adjacent cells
public:
//dfs function
    void dfs(int x,int y, vector<vector<int>>&grid){
        int n=grid.size(), m=grid[0].size();
        vis[x][y]=1; //mark the current cell (i,j) as visited
        q.push({x,y});  //and push it in the queue as we will perform the bfs from this cell later on

//now we traverse all the possible 4 adjacent cell from (i,j) and recur from there so that all cells
//connected to (i,j) s marked as visited 
        for(int d=0;d<4;d++){
            int xx = x+dir[d][0];  
            int yy= y+dir[d][1];
    //check if the new co-ordinates are valid if yes recur from there 
            if(xx>=0 && yy>=0 && xx<n && yy<m && !vis[xx][yy] && grid[xx][yy]==1)
            dfs(xx,yy,grid);
        }
    }
public:
    int shortestBridge(vector<vector<int>>& grid) {
        int n= grid.size();  //get the number of rows 
        if(n==0)return 0;  
        int m=grid[0].size(); //get the number of column

        vis.assign(n,vector<int>(m,0));  //initialize all the cells of vis as false i.e 0

//Now we try to perform dfs from one of island and mark all its connected cells as visited so the only 
//cells left with value 1 in grid remains unvisited, this way we can distinguish between two islands
        for(int i=0;i<n;i++){
            int flag=0;   //falg to keep track if we have successfully performed dfs & marked all connected cells as visited
            for(int j=0;j<m;j++){
                if(grid[i][j]==1){
                    dfs(i,j,grid);
                    flag=1;  //we have successfully perfomed dfs & marked all connected cells as visited of one island
                    break;  //break so that the other island cells remain unvisited so get distinguisged
                }
            }
            if(flag==1)  
                break;
        }

    //bfs
        int ans=0;   //ans to store the final result
        while(!q.empty()){
            int sz= q.size();   //get the size of one island that were visited andpushed in queue while performing dfs
            ans++;  //increment the ans variable as it denote  the length while traversing
            for(int i=0;i<sz;i++){
                auto node = q.front();   //get the first cell in queue
                q.pop();  //remove it from queue
                int x =node.first, y= node.second;
//now we check all possible path from popped cell and check if we reach the 2nd island or not if yes
//return ans-1 else continue our search
                for(int d=0;d<4;d++){
                    int xx = x+dir[d][0]; //new coordinate adjacent onesto (x,y)

                   int yy= y+dir[d][1];
                    if(xx<0 || yy<0 || xx>=n || y>=m)continue; //if out of bound continue

                    if(xx>=0 && y>=0 && xx<n && yy<m ){ //if valid position
        //if vis[xx][yy]==0 anf grid[xx][yy]=1 that represent cell of other 2nd island so return ans-1
                        if(!vis[xx][yy] && grid[xx][yy]==1)return ans-1;
        //else we need to continue our search by pushing the adjacent cells as 0 and mark it visited
                        else if(!vis[xx][yy] && grid[xx][yy]==0){
                            q.push({xx,yy});
                            vis[xx][yy]=1;
                        }
                    }

                }
            }
        }
        return 0; //if not reachable
    }
};
