        colCount = A[0].size();

        pair<int,int> elOnIsland = findElOnIsland();

        visited = vector<bool>(rowCount*colCount, false);
        findFirstIsland(elOnIsland.first, elOnIsland.second);
        findSecondIsland();

        int res = INT_MAX;
        for (auto el1 : firstIsland)
            for(auto el2 : secondIsland)
                res = min(res, distance(el1, el2));

