class Solution
{
	vector<vector<int>> visited;
	queue<pair<int, int>> q1;

	void bfs(vector<vector<int>> &grid, int i, int j)
	{
		queue<pair<int, int>> q;
		visited[i][j] = 1;
		q.push({i, j});
		q1.push({i, j});
		int xn[] = {1, -1, 0, 0};
		int yn[] = {0, 0, -1, 1};
		while (!q.empty())
		{
			int x = (q.front()).first;
			int y = (q.front()).second;
			q.pop();
			for (int ii = 0; ii < 4; ii++)
			{
				i = x + xn[ii];
				j = y + yn[ii];
				if (i < 0 || j < 0 || i >= grid.size() || j >= grid[0].size() || grid[i][j] == 0 || visited[i][j] == 1)
					continue;
				visited[i][j] = 1;
				q.push({i, j});
				q1.push({i, j});
			}
		}
	}
	int bfs2(vector<vector<int>> &grid)
	{
		int xn[] = {1, -1, 0, 0};
		int yn[] = {0, 0, -1, 1};
		while (!q1.empty())
		{
			int x = (q1.front()).first;
			int y = (q1.front()).second;
			q1.pop();
			for (int ii = 0; ii < 4; ii++)
			{
				int i = x + xn[ii];
				int j = y + yn[ii];
				if (i < 0 || j < 0 || i >= grid.size() || j >= grid[0].size() || visited[i][j] == 1)
					continue;
				if (grid[i][j] == 1 and visited[i][j] == 0)
					return grid[x][y];
				grid[i][j] = grid[x][y] + 1;
				visited[i][j] = 1;
				q1.push({i, j});
			}
		}
		return 0;
	}

public:
	int shortestBridge(vector<vector<int>> &grid)
	{
		int n = grid.size();
		int m = grid[0].size();

		bool bat = 0;
		visited.resize(n + 1, vector<int>(m + 1, 0));
		int i, j;
		//to take all 1's of one component;
		for (i = 0; i < n; i++)
		{
			for (j = 0; j < m; j++)
			{
				if (grid[i][j] == 1)
				{
					bfs(grid, i, j);  
					bat = 1;
					break;
				}
			}
			if (bat)
				break;
		}
		//bfs2 is to increment value
		return bfs2(grid) - 1;
	}
};
