Since the question mentions that there will always be exactly two islands, we will try to find all the points in one island and do the bfs from those points to another island. As soon as we get to another island, we reached the solution.

Update: One more optimization that we can do, instead of pushing all the nodes into the queue for BFS, we can only push the nodes which are at border.

```cpp
class Solution {
public:
    int n;
    int m;
    queue<pair<int, int>> Q;
    void dfs(int x, int y, vector<vector<int>> &mat) {
        auto isValid = [&](int A, int B) {
            return (A >= 0 && A < n && B >= 0 && B < m && mat[A][B] == 1);
        };
        Q.push({x, y});
        mat[x][y] = -1;
        int dir[] = {-1, 0, 1, 0, -1};
        for (int i = 0; i < 4; ++i) {
            int X = x + dir[i];
            int Y = y + dir[i + 1];
            if (isValid(X, Y)) {
                dfs(X, Y, mat);
            }
        }
    }
    int shortestBridge(vector<vector<int>>& grid) {
        n = grid.size();
        m = grid[0].size();
        bool haveFound = false;
        for (int i = 0; i < n; ++i) {
            for (int j = 0; j < m; ++j) {
                if (grid[i][j] == 1) {
                    dfs(i, j, grid);
                    haveFound = true;
                    break;
                }
            }
            if (haveFound)  break;
        }
        auto isValid = [&](int A, int B) {
            return (A >= 0 && A < n && B >= 0 && B < m);
        };
        int depth = -1;
        while(Q.size() > 0) {
            int N = Q.size();
            ++depth;
            while(N--) {
                const auto node = Q.front();
                Q.pop();
                int dir[] = {-1, 0, 1, 0, -1};
                for (int i = 0; i < 4; ++i) {
                    int X = node.first + dir[i];
                    int Y = node.second + dir[i + 1];
                    if (isValid(X, Y)) {
                        if (grid[X][Y] == 1) return depth;
                        if (grid[X][Y] == 0) {
                            Q.push({X, Y});
                            grid[X][Y] = -1;
                        }
                    }
                }
            }
        }
        return depth;
    }
};
