solutions.content:
-> mark all the 1's that belong to first island using dfs
->store all the points for first island so to start from here we can move toward second island
->we perform bfs so to reach 2nd island because from all the available points to be flipped we have to only flip one point in one layer and bfs allow to cover one layer in one round.
->as we reach the 1 which has not been visited before we will return the count of how may times we have performed bfs that has been stored in res variable, as this is the count of in how many layers we have flipped the value.
```
class Solution {
public:
      /*Move in four direction*/
      vector<int>dx={1,-1,0,0};
       vector<int>dy={0,0,1,-1};

    vector<vector<bool>>vis;
    vector<vector<int>>v;
     int n,m,res;
    int shortestBridge(vector<vector<int>>& A) {
         n=A.size();
         m=A[0].size();
         v=A;
         res=0;
         vis.resize(n,vector<bool>(m,false));

        /* For marking 1st island as visited*/
         for(int i=0;i<n;i++)
         {
             int f=0;
             for(int j=0;j<m;j++)
               if(v[i][j]==1){dfs(i,j);f=1;break;}  

             if(f)break;//as there are only two island and 1 is already visited
         }

         /* To store all the points corresponding to 1st island*/
         queue<pair<int,int>>qp;
        for(int i=0;i<n;i++)
            for(int j=0;j<m;j++)
                if(vis[i][j])
                    qp.push({i,j});

         bfs(qp);

        return res;
    }

    /*To find the first island*/
    void dfs(int x,int y)
    {
        vis[x][y]=true;
        for(int i=0;i<4;i++)
        {
if(check(x+dx[i],y+dy[i]) && v[x+dx[i]][y+dy[i]]==1 && vis[x+dx[i]][y+dy[i]]==false)
                dfs(x+dx[i],y+dy[i]);
        }
    }

    /*To iterate through the matrix starting from 1st island until we reach 2nd island*/
    void bfs(queue<pair<int,int>>q)
    {
        queue<pair<int,int>>tmp;//storing the points to flipped so to reach 2nd island

        while(!q.empty())
        {
            pair<int,int>p=q.front();
            q.pop();
            int x=p.first,y=p.second;
            for(int i=0;i<4;i++)
            if(check(x+dx[i],y+dy[i]) && vis[x+dx[i]][y+dy[i]]==false) 
            {
                if(v[x+dx[i]][y+dy[i]]==1)return; //we reached the second island

                vis[x+dx[i]][y+dy[i]]=true;
                tmp.push({x+dx[i],y+dy[i]});
            }
        }
        res++; // only one of all the tmp points should flipped in this layer

        bfs(tmp);
    }

    /*To check if point is valid*/
    bool check(int x,int y)
    {
        if(x>=0 &&x<n && y>=0 && y<m)return true;
        return false;
    }

};
