#define pii pair<int, int>
int fx[] = {-1, 1, 0, 0};
int fy[] = {0, 0, -1, 1};
class Solution {
public:
    int n, m, vis[105][105], dis[105][105];
    vector<vector<int>> grids;
    queue<pii> q;

    bool valid(int x, int y) {
        if(x >= 0 and x < n and y >= 0 and y < m and !vis[x][y]) return true;
        else return false;
    }

    void dfs(int x, int y) {
        if(valid(x, y) and grids[x][y]) {
            vis[x][y] = 1;
            dfs(x+1, y);
            dfs(x-1, y);
            dfs(x, y+1);
            dfs(x, y-1);
        }
    }

    void bfs() {
        while(!q.empty()) {
            pii top = q.front();
            int x = top.first;
            int y = top.second;
            q.pop();
            for(int k=0; k<4; k++) {
                int tx = x + fx[k];
                int ty = y + fy[k];
                if(valid(tx, ty)) {
                    vis[tx][ty] = 1;
                    dis[tx][ty] = dis[x][y] + 1;
                    q.push({tx, ty});
                }
            }
        }
    }

    int shortestBridge(vector<vector<int>>& grid) {
        memset(vis, 0, sizeof(vis));
        memset(dis, 0, sizeof(dis));
        grids = grid;
        n = grid.size(), m = grid[0].size();

        for(int i=0; i<n; i++) {
            int fl = 0;
            for(int j=0; j<m; j++) {
                if(grid[i][j]) {
                    dfs(i, j);
                    fl = 1;
                    break;
                }
            }
            if(fl) break;
        }

        for(int i=0; i<n; i++) {
            for(int j=0; j<m; j++) {
                if(vis[i][j]) q.push({i, j});
            }
        }

        bfs();

        int ans = INT_MAX;
        for(int i=0; i<n; i++) {
            for(int j=0; j<m; j++) {
                if(grid[i][j] and dis[i][j]) ans = min(ans, dis[i][j]-1);
            }
        }
        return ans;
    }
};
