class Solution {

    static int moves[][] = new int[][]{{0, 1}, {1, 0}, {-1, 0}, {0, -1}};

    boolean isValid(int[][] grid, int R, int C, int r, int c, boolean[][] visited) {
        return r >= 0 && r < R && c >= 0 && c < C && grid[r][c] == 1 && !visited[r][c];
    }

    boolean isValid(int R, int C, int r, int c, boolean[][] visited) {
        return r >= 0 && r < R && c >= 0 && c < C && !visited[r][c];
    }

    public int shortestBridge(int[][] grid) {

        Queue<Node> qFor1stIsland = new LinkedList<>();

        int R = grid.length;
        int C = grid[0].length;
        boolean visited[][] = new boolean[R][C];

        int firstR = -1, firstC = -1;
        for (int i = 0; i < R; i++) {
            for (int j = 0; j < C; j++) {
                if (grid[i][j] == 1) {
                    // we have found 1st island, now save these indexes and
                    // using dfs or bfs traverse all connected nodes and save them into a queue
                    firstR = i;
                    firstC = j;
                    break;
                }
            }
        }

        // bfs to traverse all connected nodes and save them into a queue qFor1stIsland
        Queue<Node> qForBFS = new LinkedList<>();
        qForBFS.add(new Node(firstR, firstC));
        visited[firstR][firstC] = true;
        while (!qForBFS.isEmpty()) {
            Node node = qForBFS.remove();
            qFor1stIsland.add(node);
            for (int[] move : moves) {
                int nextR = node.r + move[0];
                int nextC = node.c + move[1];
                if (isValid(grid, R, C, nextR, nextC, visited)) {
                    qForBFS.add(new Node(nextR, nextC));
                    visited[nextR][nextC] = true;
                }
            }
        }

        // now qFor1stIsland have all nodes from 1st island
        // now idea is to find all unvisited nodes from nodes present in qFor1stIsland and add them in another queue
        // pick this new queue and then goto unvisited nodes and the moment we find 1, we can say we have found our 2nd island
        // no of times we switch queues is our solution

        // here I am using same queue, but I am storing size of queue before I start processing so that I know when my current level is going complete

        int noOfFlip = 0;
        while (!qFor1stIsland.isEmpty()) {
            int size = qFor1stIsland.size();
            while (size > 0) {
                Node node = qFor1stIsland.remove();
                for (int[] move : moves) {
                    int nextR = node.r + move[0];
                    int nextC = node.c + move[1];
                    if (isValid(R, C, nextR, nextC, visited)) { // this method does not check grid value
                        if (grid[nextR][nextC] == 1) {
                            return noOfFlip;
                        }
                        qFor1stIsland.add(new Node(nextR, nextC));
                        visited[nextR][nextC] = true;
                    }
                }
                size--;
            }
            noOfFlip++;
        }

        return -1; // code wont reach here, if reached then it means no 2nd island
    }

    static class Node {
        int r, c;

        Node(int r, int c) {
            this.r = r;
            this.c = c;
        }
    }

}
