class Solution {
public:
    int shortestBridge(vector<vector<int>>& A) {
        // DFS + BFS: first using DFS to mark all 1s in the 1st island to be -1s, and store their indices.
        // Then for each index, using BFS to mark adjacent 0 point as -1 and store their indices as the next level.
        // We keep doing this until we find any adjacent point to be 1, that means we have built the bridge.
        int res = 0, marked = 0;
        vector<pair<int, int>> indices;
        vector<pair<int, int>> dirs{{-1, 0}, {1, 0}, {0, -1}, {0, 1}};

        for (int i = 0; i < A.size(); ++i)
        {
            for (int j = 0; j < A[0].size(); ++j)
            {
                if (A[i][j] == 1)
                {
                    dfs(A, i, j, indices); // dfs to mark all 1s in the islands to be -1s
                    marked = 1;
                    break;
                }
            }

            if (marked)
            {
                break;
            }
        }

        while (true)
        {
            vector<pair<int, int>> next;
            for (const auto& ind : indices)
            {
                int x = ind.first, y = ind.second;
                for (const auto& dir : dirs) // up, down, left and right
                {
                    int x = ind.first + dir.first;
                    int y = ind.second + dir.second;
