		A[x][y] = 2;
		int[] temp = { x, y, 0 };
		queue.offer(temp);

		dfs(A, x + 1, y, m, n, queue);
		dfs(A, x - 1, y, m, n, queue);
		dfs(A, x, y + 1, m, n, queue);
		dfs(A, x, y - 1, m, n, queue);
