class Solution {
    int r,c;
            int ans=INT_MAX;


    void dfs(vector<vector<int>>& A,int i,int j,vector<vector<bool>>& vis,vector<pair<int,int>>& S)
    {
        if(i<0||j<0||i>=r||j>=c) return;
        if(vis[i][j]||A[i][j]==0) return;

        vis[i][j]=true; 
        S.push_back(make_pair(i,j));
        dfs(A,i-1,j,vis,S);
        dfs(A,i+1,j,vis,S);
        dfs(A,i,j-1,vis,S);
        dfs(A,i,j+1,vis,S);

        return;
