        dfs(a, i-1, j, q, seen);
        dfs(a, i, j+1, q, seen);
        dfs(a, i, j-1, q, seen);
    }
    private int shortestPath(int[][] a, Queue<int[]> q, Set<String> oldIsland){
        int moves = 0, m = a.length, n = a[0].length;
        Set<String> seen = new HashSet<>();
        while(!q.isEmpty()){
            int size = q.size();
            for(int k=0; k < size; k++){
                int[] curr = q.remove();
                int i = curr[0], j = curr[1];
                String key = i+" "+j;
                if(seen.contains(key)) continue;
                if(a[i][j] == 1 && !oldIsland.contains(key)) return moves-1;
                seen.add(key);
                if(i-1 >= 0)
                    q.add(new int[]{i-1, j});
                if(i+1 < m)
                    q.add(new int[]{i+1, j});
                if(j-1 >= 0)
                    q.add(new int[]{i, j-1});
                if(j+1 < n)
                    q.add(new int[]{i, j+1});
            }
            ++moves;
        }
        return -1;
    }
