class Solution {
public:
    void dfs(int i , int j ,vector<vector<int>>& grid ,queue<vector<int>>&q ){
        if(i<0||i>=grid.size() || j<0 || j>= grid[0].size() || grid[i][j] == 2 || grid[i][j] == 0)return;
        grid[i][j] = 2;
        q.push({i,j,0});
        dfs(i , j-1 , grid ,q);
        dfs(i , j+1 , grid ,q);
        dfs(i+1 , j , grid ,q);
        dfs(i-1 , j , grid ,q);
