                if(grid[i][j]){
                    f=1;
                    dfs(i,j,grid,q);
                    break;
                }
            }
            if(f)break;
        }

        cout<<q.size()<<endl;
        int dr[4] = {0,0,-1,1};
        int dc[4] = {-1,1,0,0};
        while(!q.empty()){
            auto v = q.front();
