class Solution {
public:
    int n,ans=0;
    queue<vector<int>> q;
    vector<int> di= {-1,0,1,0} , dj= {0,1,0,-1};
    vector<vector<bool>> vis;

    bool isValid(int i,int j){
        return (i>=0 && j>=0 && i<n && j<n);
    }

    void dfs(int i,int j,vector<vector<int>>& grid){
        for(int k=0;k<4;k++){
            int ii= i+di[k], jj= j+dj[k];
            if(isValid(ii,jj) && grid[ii][jj]==1){
                q.push({ii,jj});
                vis[ii][jj]= true;
                grid[ii][jj]=0;
                dfs(ii,jj,grid);
            }
        }
    }

    int shortestBridge(vector<vector<int>>& grid) {
        n= grid.size();
        vis.resize(n, vector<bool>(n, false));
        bool done= false;

        // for DFS call only one the when first "1" is found
        for(int i=0;i<n;i++){
            if(done) break;
            for(int j=0;j<n;j++){
                if(done) break;
                if(grid[i][j]==1) {
                    q.push({i,j});
                    vis[i][j]= true;
                    grid[i][j]=0;
                    dfs(i,j,grid);
                    done= true;
                }
            }
        }


        // BFS call from all island-1
        while(!q.empty()){
            int sz= q.size();

            while(sz--){
                int i= q.front()[0], j= q.front()[1];
                q.pop();

                for(int k=0; k<4; k++){
                    int ii= i+di[k], jj= j+dj[k];
                    if(isValid(ii,jj) && vis[ii][jj]==false){
                        if(grid[ii][jj]==1) return ans;
                        vis[ii][jj]= true;
                        q.push({ii,jj});
                    }
                }
            }

            ans++;
        }
        return ans;
    }
};
