class Solution {
    private:
    void dfs(int i, int j, vector<vector<int>>& grid, queue<pair<int, int> > &q){
        int rows = grid.size();
        int cols = grid[0].size();
        if (i < 0 || j < 0 || i >= rows || j >= cols || grid[i][j] != 1){
            return;
        }

        grid[i][j] = 2;
        q.push({i, j});
        dfs(i+1, j, grid, q);
        dfs(i-1, j, grid, q);
        dfs(i, j+1, grid, q);
        dfs(i, j-1, grid, q);
    }
public:

    int dir[4][2] = {{0,1}, {1,0}, {-1, 0}, {0, -1}};

    int shortestBridge(vector<vector<int>>& grid) {
        int rows = grid.size();
        int cols = grid[0].size();

        bool flag = false;

        queue<pair<int, int> >q;

        for (int i = 0; i < rows; i++){
            for(int j = 0; j < cols; j++){
                if (grid[i][j] == 1 and !flag){
                    dfs(i, j, grid, q);
                    flag = true;
                    break;
                }
            }
            if (flag){
                break;
            }
        }
        int ans= 0;
        while(!q.empty()){
            int Qsize = q.size();
            ans++;

            while(Qsize--){
                auto node = q.front();
                q.pop();
                int x = node.first;
                int y = node.second;

                for (auto it: dir){
                    int newX = x + it[0];
                    int newY = y + it[1];

                    if (newX >= 0 && newY >= 0 && newX < rows && newY < cols){
                        if (grid[newX][newY] == 1){
                            return ans - 1;
                        }else if (grid[newX][newY] == 0) {
                            q.push({newX, newY});
                            grid[newX][newY] = 2;
                        }
                    }
                }

            }
        }

        return -1;
    }
};
