**First Find all cells with value 1 using *DFS* and then apply *BFS* to find shortest Distance**

	class Solution {
		Queue<int[]> q = new LinkedList<>();
		private int[][] dirs = new int[][]{{0, -1}, {0, 1}, {-1, 0}, {1, 0}};

		public int shortestBridge(int[][] grid) {
			boolean flag = false;

			for(int i = 0; i < grid.length && !flag; i++){
				for(int j = 0; j < grid[0].length; j++){
					if(grid[i][j] == 1){
						dfs(i, j, grid);
						flag = true;
						break;
					}
				}
			}

			while(!q.isEmpty()){
				int[] cell = q.poll();
				int i = cell[0], j = cell[1], dist = cell[2];
				for(int[] dir : dirs){
					int x = i + dir[0], y = j + dir[1];
					if(x >= 0 && x < grid.length && y >= 0 && y < grid[0].length){
						if(grid[x][y] == 1) return dist;
						if(grid[x][y] == 0) {
							q.add(new int[]{x, y, dist+1});
							grid[x][y] = 2;
						}
					}
				}
			}
			return -1;
		}
		private void dfs(int i, int j, int[][] grid){
			if(i < 0 || i >= grid.length || j < 0 || j >= grid[0].length || grid[i][j] != 1) return;
			grid[i][j] = 2;
			q.add(new int[]{i, j, 0});
			dfs(i, j-1, grid);
			dfs(i, j+1, grid);
			dfs(i-1, j, grid);
			dfs(i+1, j, grid);
		}
	}

class Solution {

public void dfs(int a[][], int i, int j) {
	if (i < 0 || i >= a.length || j < 0 ||  j >= a[0].length || a[i][j] == 0 || a[i][j] == 2) return;
	a[i][j] = 2;
	dfs(a,i-1,j);dfs(a,i+1,j);dfs(a,i,j-1);dfs(a,i,j+1);
}
public int shortestBridge(int[][] a) {
	boolean found = false;
	Queue<int[]> q = new LinkedList<>();
	for (int i = 0; i < a.length; i++) {
		for (int j = 0; j < a[0].length; j++) {
			 if (a[i][j] == 1 && !found) {
				 found = true;
				 dfs(a,i,j);
			 }
			if (found && a[i][j] == 1) q.add(new int[]{i,j});
		}
	}
	int ans = 0;
	while (!q.isEmpty()) {
		int size = q.size();
		for (int c = 0; c < size; c++) {
			int b[] = q.remove();
			int i = b[0];
			int j = b[1];
			if ((i > 0 && a[i-1][j] == 2) || (i < a.length-1 && a[i+1][j] == 2) || (j > 0 && a[i][j-1] == 2) || 
				(j < a[0].length-1 && a[i][j+1] == 2)) return ans;
			if (i > 0 && a[i-1][j] == 0) {
				a[i-1][j] = 1;
				q.add(new int[]{i-1,j});
			}
			if (i < a.length-1 && a[i+1][j] == 0) {
				a[i+1][j] = 1;
				q.add(new int[]{i+1,j});
			}
			if (j > 0 && a[i][j-1] == 0) {
				a[i][j-1] = 1;
				q.add(new int[]{i,j-1});
			}
			if (j < a[0].length-1 && a[i][j+1] == 0) {
				a[i][j+1] = 1;
				q.add(new int[]{i,j+1});
			}
		}
		ans++;
	}
	return 0;
}
}

