* ***Approach 1 : Using DFS || Brute Force***

* ***Time Complexity : O(N * N)***
* ***Space Complexity : O(N * N)***

```
    void dfs(vector<vector<int>>& grid, int i, int j, int n, vector<vector<int>>& res)
    {
        if(i < 0 || i >= n || j < 0 || j >= n || grid[i][j] == -1 || grid[i][j] == 0)
            return;

        grid[i][j] = -1;

        res.push_back({i, j});

        dfs(grid, i - 1, j, n, res);

        dfs(grid, i + 1, j, n, res);

        dfs(grid, i, j - 1, n, res);

        dfs(grid, i, j + 1, n, res);
    }

    int shortestBridge(vector<vector<int>>& grid) {

        int n = grid.size();

        vector<vector<int>> island1;

        vector<vector<int>> island2;

        bool flag = true;

        for(int i = 0; i < n; i++)
        {
            for(int j = 0; j < n; j++)
            {
                if(grid[i][j] == 1)
                {
                    if(flag)
                    {
                        dfs(grid, i, j, n, island1);

                        flag = false;
                    }
                    else
                    {
                        dfs(grid, i, j, n, island2);
                    }
                }
            }
        }

        int mini = INT_MAX;

        for(int i = 0; i < island1.size(); i++)
        {
            int x1 = island1[i][0];

            int y1 = island1[i][1];

            for(int j = 0; j < island2.size(); j++)
            {
                int x2 = island2[j][0];

                int y2 = island2[j][1];

                int d1 = abs(x1 - x2);

                int d2 = abs(y1 - y2);

                int d = d1 + d2 - 1;

                mini = min(mini, d);
            }
        }

        return mini;
    }
```


* ***Approach 2 : Using DFS && BFS***

* ***Time Complexity : O(N * N)***
* ***Space Complexity : O(N * N)***

```
    set<pair<int, int>> vis;

    void dfs(vector<vector<int>>& grid, int i, int j, int n, queue<pair<int, int>>& q)
    {
        if(i < 0 || i >= n || j < 0 || j >= n || grid[i][j] == 0 || vis.find({i, j}) != vis.end())
            return;

        vis.insert({i, j});

        q.push({i, j});

        dfs(grid, i - 1, j, n, q);

        dfs(grid, i + 1, j, n, q);

        dfs(grid, i, j - 1, n, q);

        dfs(grid, i, j + 1, n, q);
    }

    int shortestBridge(vector<vector<int>>& grid) {

        int n = grid.size();

        queue<pair<int, int>> q;

        bool flag = true;

        for(int i = 0; i < n && flag; i++)
        {
            for(int j = 0; j < n && flag; j++)
            {
                if(grid[i][j] == 1 && flag)
                {
                    dfs(grid, i, j, n, q);

                    flag = false;
                }
            }
        }

        int ans = 0;

        vector<int> dx = {0, 0, 1, -1};

        vector<int> dy = {1, -1, 0, 0};

        while(!q.empty())
        {
            int size = q.size();

            while(size--)
            {
                auto x = q.front();

                q.pop();

                int i = x.first;

                int j = x.second;

                for(int k = 0; k < 4; k++)
                {
                    int new_row = i + dx[k];

                    int new_col = j + dy[k];

                    if(new_row >= 0 && new_row <= n - 1 && new_col >= 0 && new_col <= n - 1 && vis.find({new_row, new_col}) == vis.end())
                    {
                        if(grid[new_row][new_col] == 1)
                        {
                            return ans;
                        }

                        else
                        {
                            q.push({new_row, new_col});

                            vis.insert({new_row, new_col});
                        }
                    }
                }   
            }

            ans++;
        }

        return ans;

    }
