class Solution {
    void helper(vector<vector<int>>& grid, int i, int j, vector<pair<int, int>>& v)
    {
        if (i<0 || j<0 || i>=grid.size() || j>=grid.size() || grid[i][j]!=1)
            return ;
        grid[i][j] = 2;
        v.push_back({i, j});
        for (int k=0; k<4; k++)
            helper(grid, i+arr[k], j+arr[k+1], v);
