class Solution(object):
    def shortestBridge(self, grid):
        """
        :type grid: List[List[int]]
        :rtype: int
        """

        numR = len(grid)
        numC = len(grid[0])
        directions = [(-1, 0), (1, 0), (0, -1), (0, 1)]
        visited = [[False] * numC for _ in range(numR)]
        searchQueue = deque()

        def updateIsland(r, c):
            queue = deque()
            queue.append((r, c))
            visited[r][c] = True

            while queue:
                cr, cc = queue.popleft()
                for dr, dc in directions:
                    nr = cr + dr
                    nc = cc + dc
                    if 0 <= nr < numR and 0 <= nc < numC and not visited[nr][nc]:
                        visited[nr][nc] = True
                        if grid[nr][nc] == 1:
                            queue.append((nr, nc))
                        elif grid[nr][nc] == 0:
                            searchQueue.append((nr, nc, 1))

        updated = False       
        for r in range(numR):
            for c in range(numC):
                if grid[r][c] == 1:
                    updateIsland(r, c)
                    updated = True
                    break
            if updated:
                break

        while searchQueue:
            r, c, flips = searchQueue.popleft()
            for dr, dc in directions:
                nr = r + dr
                nc = c + dc
                if 0 <= nr < numR and 0 <= nc < numC and not visited[nr][nc]:
                    if grid[nr][nc] == 1:
                        return flips
                    else:
                        searchQueue.append((nr, nc, flips + 1))
                        visited[nr][nc] = True
