class Solution:
    def shortestBridge(self, grid: List[List[int]]) -> int:
        #BFS to find the two islands and mark their cell as 2 and 3
        #Bidirectional BFS expanding two islands, record the count of two bfs levels, when one island touches the other, return the count of two bfs
        #O(n) time and space

        m, n = len(grid), len(grid[0])

        mark = 2
        islands = []
        for i in range(m):
            for j in range(n):
                if grid[i][j] == 1:
                    islands.append(self.markIsland(i, j, grid, mark))
                    mark += 1
        flip = 0
        while True:
            for island in islands:
                if self.connect(island, grid):
                    return flip
                flip += 1

    def markIsland(self, i, j, grid, mark):
        m, n = len(grid), len(grid[0])
        q = deque([(i, j)])
        grid[i][j] = mark
        islands = deque()
        while q:
            for _ in range(len(q)):
                i, j = q.pop()
                islands.appendleft((i,j))
                for x, y in [(1, 0), (0, 1), (-1, 0), (0, -1)]:
                    ni, nj = i + x, j + y
                    if 0 <= ni < m and 0 <= nj < n and grid[ni][nj] == 1:
                        grid[ni][nj] = mark
                        q.appendleft((ni, nj))
        return islands

    def connect(self, island, grid):
        m, n = len(grid), len(grid[0])
        for _ in range(len(island)):
            i, j = island.pop()
            for x, y in [(1, 0), (0, 1), (-1, 0), (0, -1)]:
                ni, nj = i + x, j + y
                if 0 <= ni < m and 0 <= nj < n:
                    if grid[ni][nj] == grid[i][j]:
                        continue
                    elif grid[ni][nj] == 0:
                        grid[ni][nj] = grid[i][j]
                        island.appendleft((ni, nj))
                    else:
                        return True
        return False






