                    if(u < 0 or v < 0 or u >= m or v >= n or g[u][v] == 2)
                        continue;

                    if(g[u][v]==1) return cnt-1;

                    g[u][v]=2;
                    que.push({u,v});
                }
            }
        }
        return 0;
    }
    int shortestBridge(vector<vector<int>>& A) {
        int m=A.size();
        int n=A[0].size();
        queue<pair<int,int>>q;
        bool istrue=false;
        int ans=0;
        for(int i=0;i<m;i++){
            for(int j=0;j<n;j++){
                if(A[i][j]==1){
                    dfs(A,q,i,j,m,n);
                    istrue=true;
                    break;
                }
            }
            if(istrue) break;
        }

        ans=bfs(A,q,m,n);

        return ans;
    }
};
