1. (DFS) We start with adding the positions of zeroes (the edges) around the first island we find to the queue
2. (BFS) We keep "expanding the island" until we hit the other island
```
private static final int[][] DIRS = new int[][]{{1,0},{-1,0},{0,1},{0,-1}};

public int shortestBridge(int[][] grid) {
	int R = grid.length, C = grid[0].length;
	Queue<int[]> queue = new LinkedList();
	boolean[][] visited = new boolean[R][C];
	boolean found = false;
	for (int r = 0; !found && r < R; r++) {
		for (int c = 0; !found && c < C; c++) {
			if (grid[r][c] == 1) {
				dfs(queue, grid, R, C, r, c, visited);
				found = true;
			}
		}
	}
	int step = 0;
	while (!queue.isEmpty()) {
		step++;
		for (int i = queue.size() - 1; i >= 0; i--) {
			int[] p = queue.poll();
			for (int d = 0; d < DIRS.length; d++) {
				int nr = p[0] + DIRS[d][0], nc = p[1] + DIRS[d][1];
				if (nr >= 0 && nr < R && nc >= 0 && nc < C && !visited[nr][nc]) {
					if (grid[nr][nc] == 1) {
						return step;
					}
					visited[nr][nc] = true;
					queue.add(new int[]{nr, nc});
				}
			}
		}
	}
	return -1;
}

private void dfs(Queue<int[]> queue, int[][] grid, int R, int C, int r, int c, boolean[][] visited) {
	if (visited[r][c]) {
		return;
	}
	visited[r][c] = true;
	for (int i = 0; i < DIRS.length; i++) {
		int nr = r + DIRS[i][0], nc = c + DIRS[i][1];
		if (nr >= 0 && nr < R && nc >= 0 && nc < C) {
			if (grid[nr][nc] == 1) {
				dfs(queue, grid, R, C, nr, nc, visited);
			} else if (!visited[nr][nc]) {
				visited[nr][nc] = true;
				queue.add(new int[]{nr, nc});
			}
		}
	}
}
