```
class Node{
    int r,c;
    public Node(int r, int c){
        this.r = r;
        this.c = c;
    }
}
class Solution {
    // val = +/-1 -> for island 1
    //       +/-2 -> for island 2
    // Minus sign for denoting a border
    // border is defined as cell whose next cell (either from the 4 directions) contains water i.e 0

    // Directions
    int[] x = {-1,1,0,0}; // left, right, up, down
    int[] y = {0,0,1,-1};

    // filling island with val i.e visiting all adjacent island and marking it with "val"
    public boolean dfs(int[][] grid, int i, int j, int n, int val){
        if(i<0||i>=n || j<0||j>=n || grid[i][j]==val || grid[i][j]==-val){ // return false, if cell is visited i.e either border or under border land
            return false;
        }
        if(grid[i][j]==0)
            return true;
        // visit only those cells which are 1
        grid[i][j] = val;
        for(int k=0;k<4;k++){
            if(dfs(grid, i+y[k], j+x[k], n, val)) // if dfs returns true means next cell is water, so (i,j) must be border
                grid[i][j] = -val;
        }
        return false;
    }
    public int shortestBridge(int[][] grid) {
        if(grid==null || grid.length < 2)
            return -1;
        int val = 2; // for island 1
        //  under border area will be +val
        //  border will  be -val
        // Example -> 
        /*
             2 -2  2 -2  2
            -2  0 -2  0 -2
             0  0  0  0  0
            -3 -3  0 -3 -3
             3  3 -3  3  3

             Border of island 1 = (1,0) (0,1) (1,2) (0,3) (1,4)
             Border of island 2 = (3,0) (3,1) (4,2) (3,3) (3,4)
             Distance from each border = abs(r1-r2) + abs(c1-c2); // since one cell has four options to move
        */
        int n = grid.length;
        List<Node> border_1 = new LinkedList<>();
        List<Node> border_2 = new LinkedList<>();
        // storing border of islands
        for(int i=0;i<n;i++){
            for(int j=0;j<n;j++){
                if(grid[i][j]==1){
                    dfs(grid, i, j, n, val);
                    val++;
                }
                if(grid[i][j]==-2)
                    border_1.add(new Node(i, j));
                if(grid[i][j]==-3)
                    border_2.add(new Node(i, j));
            }
        }
        int min = Integer.MAX_VALUE;
        Iterator<Node> b1 = border_1.iterator();
        while(b1.hasNext()){
            Node island1 = b1.next();
            Iterator<Node> b2 = border_2.iterator();
            while(b2.hasNext()){
                Node island2 = b2.next();
                int dist = Math.abs(island1.r-island2.r) + Math.abs(island1.c-island2.c);
                min = Math.min(min, dist);
            }
        }
        return min-1; // minus 1 because the distance will contain border also
    }
}
