                if(grid[i][j]==1){
                    fill(grid, i, j, n, queue);
                    found = true;
                    break;
                }
            }
        }

        int level = 0; // search level == approaching distance to next island
        while(!queue.isEmpty()){
            int size = queue.size();
            while(size-->0){
                Node node = queue.poll();
                for(int k=0;k<4;k++){
                    int i = node.r + y[k];
