```
class Solution {
    public int shortestBridge(int[][] A) {

        boolean marked=false;
        Queue<Pair<Integer,Integer>> q=new LinkedList<>();

        int bridge=0;

        for(int i=0;i<A.length;i++){

            if(marked) break;

            for(int j=0;j<A[0].length;j++){
                if(A[i][j]==1){
                    dfs(A,i,j,q);
                    marked=true;
                }
                if(marked) break;
            }
        }

        while(!q.isEmpty()){
            int n=q.size();

            System.out.println("here"+n);

            for(int i=0;i<n;i++){
                Pair<Integer,Integer> pair=q.poll();

                int r=pair.getKey();
                int c=pair.getValue();

                if(r+1<A.length && A[r+1][c]==0){
                    q.add(new Pair(r+1,c));
                    A[r+1][c]=2;
                }
                if(r-1>=0 && A[r-1][c]==0){
                    q.add(new Pair(r+1,c));
                    A[r-1][c]=2;
                }
                if(c+1<A[0].length && A[r][c+1]==0){
                    q.add(new Pair(r,c+1));
                    A[r][c+1]=2;
                }
                if(c-1>=0 && A[r][c-1]==0){
                    q.add(new Pair(r,c-1));
                    A[r][c-1]=2;
                }

                if((r+1<A.length && A[r+1][c]==1) || 
                   (r-1>=0 && A[r-1][c]==1) || 
                   (c+1<A[0].length && A[r][c+1]==1)|| 
                   (c-1>=0 && A[r][c-1]==1)) return bridge;

            }
            bridge++;
        }

        return 0;
