        for (int i = 0; i < a.length; i++) {
            for (int j = 0; j < a.length; j++) {
                if (a[i][j] != 1) continue;
                dfs(a, i, j);
                return bfs(a);
            }
        }

        throw new RuntimeException();
    }

    private void dfs(int[][] a, int i, int j) {
        if (i < 0 || j < 0 || i == a.length || j == a.length || a[i][j] != 1) return;

        a[i][j] = 2;

        dfs(a, i - 1, j);
        dfs(a, i + 1, j);
        dfs(a, i, j - 1);
        dfs(a, i, j + 1);
    }

    private int bfs(int[][] a) {
        int currentLayer = 1;
        while (true) {
            currentLayer++;
            for (int i = 0; i < a.length; i++) {
                for (int j = 0; j < a.length; j++) {
                    if (a[i][j] == currentLayer) {
                        if (
                            (i > 0 && a[i-1][j] == 1) ||
                            (i < a.length-1 && a[i+1][j] == 1) ||
                            (j > 0 && a[i][j-1] == 1) ||
