class Solution {

    private static final int[][] DIRECTIONS = new int[][] {{-1, 0}, {1, 0}, {0, 1}, {0, -1}};
    private static final int ISLAND1 = 2;
    private static final int ISLAND2 = 3;

    public int shortestBridge(int[][] islands) {
        if (islands == null || islands.length == 0 || islands[0] == null || islands[0].length == 0) return 0;
        //Step1: Paint all the island into two distinct island (island1 & island2) using DFS
        paintAllIslands(islands);
        //Step2: Get queue populated with all the nodes in island 1
        Queue<int[]> queue = getInitialQueue(islands, ISLAND1);
        //Step3: Get the shortest path to island 2 from each nodes of island1
        return getShortestPath(islands, queue, ISLAND1, ISLAND2);
    }

    private int getShortestPath(int[][] islands, Queue<int[]> queue, int sourceIsland, int targetIsland) {
        int m = islands.length, n = islands[0].length;
        while (!queue.isEmpty()) {
            int[] u = queue.poll();
            int i = u[0], j = u[1];
            for (int[] dir : DIRECTIONS) {
                int x = dir[0] + i, y = dir[1] + j;
                if (x < 0 || y < 0 || x >= m || y >= n || islands[x][y] == sourceIsland) continue;
                if (islands[x][y] == targetIsland) return u[2];
                queue.offer(new int[] {x, y, 1 + u[2]});
                islands[x][y] = sourceIsland;
            }
        }
        return 0;
    }

    private Queue<int[]> getInitialQueue(int[][] islands, int island) {
        int m = islands.length, n = islands[0].length;
        Queue<int[]> queue = new LinkedList<>();
        for (int i = 0; i < islands.length; i++) {
            for (int j = 0; j < islands[0].length; j++) {
                if (islands[i][j] == island) queue.offer(new int[] {i, j, 0});
            }
        }
        return queue;
    }

    private void paintAllIslands(int[][] islands) {
        int m = islands.length, n = islands[0].length, color = ISLAND1;
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                if (islands[i][j] == 1) {
                    paintIsland(islands, i, j, color++);
                    if (color == 4) return;
                }
            }
        }
    }

    private void paintIsland(int[][] islands, int i, int j, int color) {
        int m = islands.length, n = islands[0].length;
        if (i < 0 || j < 0 || i >= m || j >= n || islands[i][j] != 1) return;
        islands[i][j] = color;
        for (int[] dir : DIRECTIONS) {
            int x = dir[0] + i, y = dir[1] + j;
            paintIsland(islands, x, y, color);
        }
    }
}
