    def shortestBridge(self, grid: List[List[int]]) -> int:
        rl, cl = len(grid), len(grid[0])
        d = ((1,0),(0,1),(-1,0),(0,-1))
        memo = [[0 for _ in range(cl)] for _ in range(rl)]
        q = collections.deque()

        def dfs(x,y):
            memo[x][y] = 1
            q.append((0,x,y))
            for i in d:
                nx = x + i[0]
                ny = y + i[1]
                if 0 <= nx < rl and 0 <= ny < cl and grid[nx][ny] == 1 and memo[nx][ny] == 0:
                    memo[nx][ny] = 1
                    dfs(nx, ny)

        flag = 0
        for i in range(rl):
            for j in range(cl):
                if grid[i][j] == 1:
                    dfs(i,j)
                    flag = 1
                    break
            if flag:
                break

        while q:
            t,x,y = q.popleft()
            for i in d:
                nx = x + i[0]
                ny = y + i[1]
                if 0 <= nx < rl and 0 <= ny < cl and memo[nx][ny] == 0:
                    if grid[nx][ny] == 0:
                        q.append((t + 1, nx, ny))
                        memo[nx][ny] = 1
                    else:
                        return t
