class Solution {
public:
    int dir[4][2]={{1,0},{0,1},{0,-1},{-1,0}};
    bool vis[101][101];
    void dfs(int i,int j,vector<vector<int>>& grid){
    int n=grid.size();
    stack<pair<int,int>>st;
    st.push({i,j});

    while(!st.empty()) {
        int x=st.top().first;
        int y=st.top().second;
        st.pop();

        if (x<0 or y<0 or x>=n or y>=n or grid[x][y]==0) continue;

        for(int idx=0;idx<4;idx++){
            int a=x+dir[idx][0];
            int b=y+dir[idx][1];

            if (a>=0 and a<n and b>=0 and b<n and grid[a][b]==1 and !vis[a][b]) {
                st.push({a,b});
                vis[a][b]=true;
            }
        }
    }
}
#define arr array<int,3>
    int solve(int i,int j,pair<int,int>&to,vector<vector<int>>& grid){
        int x=to.first,y=to.second;
        int n=grid.size();

        priority_queue<arr,vector<arr>,greater<arr>>pq;

        vector<vector<int>>dist(n,vector<int>(n,1e8));

        dist[i][j]=0;

        pq.push({0,i,j});

        while(!pq.empty()){
            auto temp=pq.top();
            pq.pop();

            int dis=temp[0];
            int r=temp[1],c=temp[2];

            if(grid[r][c]==0) dis++;

            for(int idx=0;idx<4;idx++){
                int a=r+dir[idx][0];
                int b=c+dir[idx][1];

                if(a>=0 and b>=0 and a<n and b<n){
                    if(dist[a][b]>dis){
                        dist[a][b]=dis;
                        pq.push({dis,a,b});
                    }
                }
            }
        }
        return dist[x][y];

    }
    int shortestBridge(vector<vector<int>>& grid) {
        int n=grid.size();

        memset(vis,false,sizeof vis);

        pair<int,int>from,to;

        bool flag=true;

        for(int i=0;i<n;i++){
            for(int j=0;j<n;j++){
                if(grid[i][j]==1 and !vis[i][j]){
                    if(flag) from={i,j},flag=false;
                    else to={i,j};
                    dfs(i,j,grid);
                }
            }
        }
        memset(vis,false,sizeof vis);

        int i=from.first,j=from.second;

        int ans=solve(i,j,to,grid);
        // cout<<ans;

        return ans;
    }
};
