```Rust
impl Solution {
    pub fn sort_string(s: String) -> String {
        let mut arr = vec![0; 26];
        for c in s.chars() {
            arr[c as usize - 'a' as usize] += 1;
        }
        let mut res = String::from("");
        let mut cter = s.len();
        let mut pivot = 0;

        while cter > 0 {
            for i in 0..26 {
                let j = if pivot == 0 {i as usize} else {(25 - i) as usize};
                if arr[j] > 0 {
                    arr[j] -= 1;
                    res.push((j as u8 + 97) as char);
                    cter -= 1;
                }
            }
            pivot = 1 - pivot;
        }
        res
    }
}

