class Solution {
public:
    string sortString(string s) {
        int count[26]={0};
        string ans="";
        // first of all store the frequency in the array
        for(int i=0;i<s.length();i++) {
            count[s[i]-'a']++;
        }
        while(true) {
            bool found=false;
            // move from 'a' to 'z' and append if ith character if present
            for(int i=0;i<26;i++) {
                if(count[i]>0) {
                    found=true;
                    ans.push_back('a'+i);
                    count[i]--;
                }
            }
            // if all the characters are used then terminate
            if(!found) {
                break;
            }
            found=false;
            // similarly move in the backward direction and append the characters
            for(int i=25;i>=0;i--) {
                if(count[i]>0) {
                    found=true;
                    ans.push_back('a'+i);
                    count[i]--;
                }
            }
            // if all the characters are used then terminate
            if(!found) {
                break;
            }
        }
        return ans;
    }
};
