class Solution {
public:
    string sortString(string s) {
        // Map used for storing all chars in the input string s
        // The map will order the chars for us in ascending order
        map<char, int> letters;
        // Store the length of s
        int n = s.length();
        // Add all chars to the map
        for(int i = 0; i < n; i++)
            letters[s[i]]++;

        // Result answer vector
        string ans = "";
        // Only loop through for the length of the original string
        while(n != 0){
            // Create an iterator for the map
            map<char, int> :: iterator itr;
            // Loop through the map forwards, adding each char and decrementing its count
            for(itr = letters.begin(); itr != letters.end(); itr++){
                if(itr->second != 0){
                    ans += itr->first;
                    itr->second = itr->second - 1;
                    n--;
                }
            }
            // Create a reverse iterator for the map
            map<char, int> :: reverse_iterator it;
            // Loop through the map backwards, adding each char and decrementing its count
            for(it = letters.rbegin(); it != letters.rend(); it++){
                if(it->second != 0){
                    ans += it->first;
                    it->second = it->second - 1;
                    n--;
                }
            }
        }
        return ans;
    }
};
