    def sortString(self, s: str) -> str:
        storage = {}
        for ch in s:
            if ch in storage:
                storage[ch] += 1
            else:
                storage[ch] = 1
        result = []
        smallest = True
        while storage:
            keys = [key for key in storage]
            keys = sorted(keys) if smallest else sorted(keys, reverse=True)
            smallest = not smallest
            for key in keys:
                result.append(key)
                if storage[key] == 1:
                    del storage[key]
                else:
                    storage[key] -= 1
        return ''.join(result)

