class Solution {
    public String sortString(String s) {
        int N = s.length();
        int[] counts = new int[26];
        int i, total = N;

        for(i = 0; i < N; i++){
            counts[s.charAt(i) - 'a']++;
        }

        StringBuilder builder = new StringBuilder();

        boolean forward = true;
        while(total > 0){
            if(forward){
                for(i = 0; i < 26; i++){
                    if(counts[i] != 0){
                        builder.append((char)('a' + i));
                        --counts[i];
                        --total;
                    }
                }
            }else{
                for(i = 25; i >= 0; i--){
                    if(counts[i] != 0){
                        builder.append((char)('a' + i));
                        --counts[i];
                        --total;
                    }
                }
            }

            forward = !forward;
        }

        return builder.toString();
    }
}

