class Solution {
    public String sortString(String s) {
        int[] count = new int[26];
        int i = 0, last = 0;
        do {
            ++count[s.charAt(i) - 'a'];
        } while (++i < s.length());

        StringBuilder res = new StringBuilder(s.length());
        do {
            for(i = last; i < 26; ++i) {
                if(count[i] > 0) {
                    res.append((char) (i + 'a'));
                    last = i;
                    --count[i];
                }
            }
            for(i = last; i >=0; --i) {
                if(count[i] > 0) {
                    res.append((char) (i + 'a'));
                    last = i;
                    --count[i];
                }
            }
        } while (res.length() < s.length());

        return res.toString();
    }
}
