		letterCount[s.charAt(i) - 'a']++;

	var wavy = new StringBuilder();
	while (wavy.length() < s.length()) {
		for (var i = 0; i < 26; i++) 
			append(letterCount, i, wavy);
		for (var i = 25; i >= 0; i--)
			append(letterCount, i, wavy);
	}

	return wavy.toString();
}

private void append(int[] letterCount, int i, StringBuilder wavy) {
	if (letterCount[i]-- < 1)
		return;
	wavy.append((char) ('a' + i));
}
