        		if(temp.charAt(x)-output.charAt(output.length()-1)>0)
        		{
        			output.append(temp.charAt(x));
        			i++;
        		}
        		// extracting the remaining string
        		else {
        			s = s+temp.charAt(x);
        		}
        	}
        	if(i == length)break;
        	// appending the largest to the result
        	output.append(s.charAt(s.length()-1));
        	i++;
        	// finding the largest character which is smaller then last appended character
