class Solution {
public:
    string sortString(string s) {
        map<char, int> mp;
        for(const char c : s) {
            ++mp[c];
        }
        string ret;
        while(!mp.empty()) {
            for(auto it = mp.begin(); it != mp.end();) {
                if(it->second == 0) {
                    it = mp.erase(it);
                    continue;
                }
                ret += it->first;
                if(--it->second == 0) {
                    it = mp.erase(it);
                } else {
                    ++it;
                }
            }

            for(auto it = mp.rbegin(); it != mp.rend(); ++it) {
                ret += it->first;
                --it->second;
            }            
        }
        return ret;
    }
};
