class Solution {
    public String sortString(String s) {
        StringBuilder result = new StringBuilder();

		//All the characters from 's' is traversed and its count is updated in charArr as below
        int[] charArr = new int[26];
        for(int i=0;i<s.length();i++){
            charArr[s.charAt(i)-'a']++;
        }

		//until the stringbuilder's length is equal to 's' length, we traverse forward and backward and push characters where we find count>0 and decrement the count in charrArr, we do forward and backward since the array will hold the count of sorted character.

        boolean isAscending = true;
        int i=0;
        while(result.length()<s.length()){
			//on forward when pointer reaches one after the end we reset it to end and move reverse
            if(isAscending && i==26){
                i=25;
                isAscending = false;
            }
			//on reverse when pointer reaches one before the start we reset it to start and move forward
            if(!isAscending && i==-1){
                i=0;
                isAscending = true;
            }
            if(charArr[i]>0){
                result.append((char)(i+'a'));
                charArr[i]--;
            }
            if(isAscending){
                i++;
            }
            else{
                i--;
            }
        }
        return result.toString();
    }
}
