class Solution {
    public String sortString(String s) {
        int totalChar=s.length();
        int counter[]=new int[26];

        for(char ch:s.toCharArray()){
            counter[ch-'a']++;
        }
        int i=0;

        //this flag will help to tell us the direction in which we need to traverse
        boolean reverse=false;

        //using StringBuilder because it takes less time to update as compared to String
        StringBuilder sb=new StringBuilder();

        //since the total number of characters in output will be same as input
        while(i<totalChar){

            //initially traversing string left to right
            if(reverse==false){
                for(int j=0;j<26 && i<totalChar;j++){
                    if(counter[j]!=0){
                        counter[j]--;
                        i++;
                        sb.append((char)('a'+j));
                    }
                }
                //since we traverse till the end of the list, reversing the direction
                reverse=!reverse;
            }

             //traversing string right to left
            if(reverse==true){
                for(int j=25;j>=0 && i<totalChar;j--){
                    if(counter[j]!=0){
                        counter[j]--;
                        i++;
                        sb.append((char)('a'+j));
                    }
                }
                reverse=!reverse;
            }
        }
        return sb.toString();
    }
}

