var sortString = function(s) {
	const map = {};
	const sort = [...new Set(s)].sort((a, b) => a.localeCompare(b));
	let increase = true;
	let result = '';

	[...s].forEach(s => map[s] = s in map ? map[s] + 1 : 1)

	while (result.length < s.length) {
		result += sort[increase ? 'reduce' : 'reduceRight']((acc, curr) => {
			if (map[curr]) {
				map[curr] -= 1
				return acc + curr
			} 
			else return acc
		}, '')
		increase = !increase
	}
	return result
};

