class Solution {
    public String sortString(String s) {
        // To store the values of the 26 letter alphabets
		int[] ch = new int[26];

		//loop through the string and store the value in the correct index in the ch array
		// (ch-97) is to get the index between 0 to 25 to fit in the ch array
		// This is needed to count the frequency of each character available
        for (int i = 0;i<s.length();i++){
            int index = s.charAt(i) - 97;
            ch[index]++;
        }

		StringBuilder sb = new StringBuilder();

        int i = 0;
        int j = 0;

        //loop through the length of the string
		// i - varible to track a to z index in ch array
		// j - variable for tracking string index
        while (j < s.length()){
            // loop from a to z index and append appropriate index to the string builder
			// ch[i]-- is to reduce the frequency
            while (i < ch.length){
                if (ch[i]-- > 0)
                    sb.append((char)(97+i));
                i++;
            }
            i--;

            // loop from z to a index
            while (i >= 0){
                if (ch[i]-- > 0)
                    sb.append((char)(97+i));
                i--;
            }
            i++;
            j = sb.toString().length(); //modified from j++ as per the comment below
        }
        return sb.toString();
    }
}
