class Solution:
    def sortString(self, s: str) -> str:
        dic = Counter(s)
        li = sorted(dic.keys())
        ret = ''
        while len(ret) < len(s):
            for c in [*li, *li[::-1]]:
                if dic[c]:
                    ret += c
                    dic[c] -= 1
        return ret
