class Solution {
public String sortString(String str) {
    StringBuilder sb = new StringBuilder("");
	int[] map = new int[26];
    char[] arr = str.toCharArray();
	for (int i=0;i<arr.length;i++)
		map[arr[i] - 'a']++;

	boolean flag = true;
    //Once all the characters are exhauseted the flag cant be reverted back to true as it cant enter the if condition.
	while (flag) {
		flag = false;
        //Iterating for the increasing string.
		for (int i = 0; i < 26; i++) {
			if(map[i]!=0) {
				sb.append((char)(i+'a'));
				map[i]--;
				flag = true;
			}
		}
        //Iterating for the decreasing string.
		for (int i = 25; i >= 0; i--) {
			if(map[i]!=0) {
				sb.append((char)(i+'a'));
				map[i]--;
				flag = true;
			}
		}

	}

	return sb.toString();
}
}
