class Solution {
public:
    string sortString(string s) {
        // store the characters in map, map sorts the characters automatically
        map<char, int> mp;
        int n = s.length();
        for(int i = 0; i < n; i++) // count the frequency of the characters in the given string
            mp[s[i]]++;
        string ans = "";
        while(n != 0){ // keep a check for the length of the string
            map<char, int> :: iterator itr;
            for(itr = mp.begin(); itr != mp.end(); itr++){ // do according to the mentioned algorithm, first pick out the smallest character in the given string, keep appending till all characters are not covered
                if(itr->second != 0){
                    ans += itr->first;
                    itr->second = itr->second - 1;
                    n--;
                }
            }
            map<char, int> :: reverse_iterator it;
            for(it = mp.rbegin(); it != mp.rend(); it++){ // now go reverse and do according to the algorithm stated, keep appending from the end
                if(it->second != 0){
                    ans += it->first;
                    it->second = it->second - 1;
                    n--;
                }
            }
        }
        return ans;
    }
};
