function sortString($s) {
	$s = str_split($s);
	sort($s);

	$res = '';
	while(count($s)) {
		$prev = '';

		foreach($s as $key => $char) {
			if($char == $prev) {
				continue;
			}

			$prev = $char;
			$res .= $char;
			unset($s[$key]);
		}

		$s = array_reverse($s);
	}

	return $res;
}
