        L.set(node, 1 + (last && last.left != node ? (R.get(last) || 0) : 0));
        R.set(node, 1 + (last && last.right != node ? (L.get(last) || 0) : 0));
        go(node.left, node);
        go(node.right, node);
    };
    go();
    return Math.max(...L.values(), ...R.values()) - 1;
};
